const css = require("css");
const { specificity, compare } = require("./specificity.js");
const layout = require("./layout.js");

const EOF = Symbol("EOF");

let currentToken = null;
let currentAttribute = null;
let currentTextNode = null;
let stack;
let rules = [];

function addCSSRules(text) {
  let ast = css.parse(text);
  rules.push(...ast.stylesheet.rules);
}

function computeCSS(element) {
  let elements = stack.slice().reverse();
  if (!element.computedStyle) {
    element.computedStyle = {};
  }

  for (let rule of rules) {
    var selectorParts = rule.selectors[0].split(" ").reverse();
    if (!match(element, selectorParts[0])) {
      continue;
    }

    let matched = false;

    let selectorLocation = 1;
    for (
      var elementLocation = 0;
      elementLocation < elements.length;
      elementLocation++
    ) {
      if (match(elements[elementLocation], selectorParts[selectorLocation])) {
        selectorLocation++;
      }
    }

    if (selectorLocation >= selectorParts.length) {
      matched = true;
    }

    if (matched) {
      var sp = specificity(rule.selectors[0]);
      let computedStyle = element.computedStyle;

      for (let declaration of rule.declarations) {
        if (!computedStyle[declaration.property]) {
          computedStyle[declaration.property] = {};
        }

        if (!computedStyle[declaration.property].specificity) {
          computedStyle[declaration.property].value = declaration.value;
          computedStyle[declaration.property].specificity = sp;
        } else if (
          compare(computedStyle[declaration.property].specificity, sp) < 0
        ) {
          computedStyle[declaration.property].value = declaration.value;
          computedStyle[declaration.property].specificity = sp;
        }
      }

      // console.log(
      //   "\n\n******************computeCSS -> computedStylel",
      //   "\ntagName=",
      //   element.tagName,
      //   "\nattribute=\n",
      //   element.attributes,
      //   "\ncomputedStyle=\n",
      //   element.computedStyle
      // );
    }
  }
}

function match(element, selector) {
  if (!selector || !element.attributes) return false;

  if (selector.charAt(0) === "#") {
    var attr = element.attributes.filter(attr => attr.name === "id")[0];
    if (attr && attr.value === selector.replace("#", "")) {
      return true;
    }
  } else if (selector.charAt(0) === ".") {
    var attr = element.attributes.filter(attr => attr.name === "class")[0];
    if (attr && attr.value === selector.replace(".", "")) {
      return true;
    }
  } else {
    if (element.tagName === selector) {
      return true;
    }
  }

  return false;
}
function emit(token) {
  // console.log("token=", token);

  let top = stack[stack.length - 1];

  if (token.type === "startTag") {
    let element = {
      type: "element",
      children: [],
      attributes: []
    };

    element.tagName = token.tagName;

    for (let p in token) {
      if (p !== "type" && p !== "tagName") {
        element.attributes.push({
          name: p,
          value: token[p]
        });
      }
    }

    computeCSS(element);

    top.children.push(element);
    // to do hhe TypeError: Converting circular structure to JSON
    // element.parent = top;

    if (!token.isSelfClosing) {
      stack.push(element);
    }

    currentTextNode = null;
  } else if (token.type === "endTag") {
    // console.log("top.tagName=", top.tagName, " token.tagName=", token.tagName);
    if (top.tagName != token.tagName) {
      throw new Error("Tag start end doesn't match!");
    } else {
      // add css operation rule
      if (top.tagName === "style") {
        addCSSRules(top.children[0].content);
      }

      layout(top);

      stack.pop();
    }
    currentTextNode = null;
  } else if (token.type == "text") {
    if (currentTextNode == null) {
      currentTextNode = {
        type: "text",
        content: ""
      };
      top.children.push(currentTextNode);
    } else {
      currentTextNode.content += token.content;
    }
  }
}

function data(c) {
  if (c === "<") {
    return tagOpen;
  } else if (c === EOF) {
    emit({
      type: "EOF"
    });
    return;
  } else {
    emit({
      type: "text",
      content: c
    });
    return data;
  }
}

function tagOpen(c) {
  if (c === "/") {
    return endTagOpen;
  } else if (c.match(/^[a-zA-Z]$/)) {
    currentToken = {
      type: "startTag",
      tagName: ""
    };
    return tagName(c);
  } else {
    emit({ type: "text", content: c });
    return data;
  }
}

function endTagOpen(c) {
  if (c.match(/^[a-zA-Z]$/)) {
    currentToken = {
      type: "endTag",
      tagName: ""
    };
    return tagName(c);
  } else if (c === ">") {
    // something wrong
  } else if (c === EOF) {
    // wrong again
  } else {
  }
}

function tagName(c) {
  if (c.match(/^[\t\n\f ]$/)) {
    return beforeAttributeName;
  } else if (c === "/") {
    return selfClosingStartTag;
  } else if (c.match(/^[a-zA-Z]$/)) {
    currentToken.tagName += c;
    return tagName;
  } else if (c === ">") {
    emit(currentToken);
    return data;
  } else {
    // should never be here ???
    return tagName;
  }
}

function beforeAttributeName(c) {
  if (c.match(/^[\t\n\f ]$/)) {
    return beforeAttributeName;
  } else if (c === ">" || c === "/" || c === EOF) {
    return afterAttributeName(c);
  } else if (c === "=") {
  } else {
    currentAttribute = {
      name: "",
      value: ""
    };
    return attributeName(c);
  }
}

function attributeName(c) {
  if (c.match(/^[\t\n\f ]$/) || c === ">" || c === "/" || c === EOF) {
    return afterAttributeName(c);
  } else if (c === "=") {
    return beforeAttributeValue;
  } else if (c === "\u0000") {
  } else if (c === '"' || c === "'" || c === "<") {
  } else {
    currentAttribute.name += c;
    return attributeName;
  }
}

function selfClosingStartTag(c) {
  if (c === ">") {
    currentToken.isSelfClosing = true;
    emit(currentToken);
    return data;
  } else if (c === "EOF") {
    // something wrong
  } else {
    // something wrong
  }
}

function afterAttributeName(c) {
  if (c.match(/^[\t\n\f ]$/)) {
    //hhe
    return afterAttributeName;
  } else if (c === "/") {
    return selfClosingStartTag;
  } else if (c === "=") {
    return beforeAttributeValue;
  } else if (c === ">") {
    currentToken[currentAttribute.name] = currentAttribute.value;
    emit(currentToken);
    return data;
  } else if (c === EOF) {
    // return data;
  } else {
    currentToken[currentAttribute.name] = currentAttribute.value;
    currentAttribute = {
      name: "",
      value: ""
    };

    return afterAttributeName(c);
  }
}

function beforeAttributeValue(c) {
  if (c.match(/^[\t\n\f ]$/) || c === ">" || c === "/" || c === EOF) {
    return beforeAttributeValue;
  } else if (c === '"') {
    return doubleQuotedAttributeValue;
  } else if (c === "'") {
    return singleQuotedAttributeValue;
  } else if (c === ">") {
    // return data;
  } else {
    return UnqotedAttributeValue(c);
  }
}

function doubleQuotedAttributeValue(c) {
  if (c == '"') {
    currentToken[currentAttribute.name] = currentAttribute.value;
    return afterQuotedAttributeValue;
  } else if (c === "\u0000") {
  } else if (c === EOF) {
  } else {
    currentAttribute.value += c;
    return doubleQuotedAttributeValue;
  }
}

function singleQuotedAttributeValue(c) {
  if (c === "'") {
    currentToken[currentAttribute.name] = currentAttribute.value;
    return afterQuotedAttributeValue;
  } else if (c === "\u0000") {
  } else if (c === EOF) {
  } else {
    currentAttribute.value += c;
    // todo hhe old doubleQuotedAttributeValue
    return singleQuotedAttributeValue;
  }
}

function afterQuotedAttributeValue(c) {
  if (c.match(/^[\t\n\f ]$/)) {
    return beforeAttributeName;
  } else if (c === "/") {
    // hhe added by myself
    currentToken[currentAttribute.name] = currentAttribute.value;
    return selfClosingStartTag;
  } else if (c === ">'") {
    currentToken[currentAttribute.name] = currentAttribute.value;
    emit(currentToken);
    return data;
  } else if (c === EOF) {
    // return data;
  } else {
    currentAttribute.value = +c;
    //hhe to do
    return doubleQuotedAttributeValue(c);
  }
}

function UnqotedAttributeValue(c) {
  if (c.match(/^[\t\n\f ]$/)) {
    currentToken[currentAttribute.name] = currentAttribute.value;
    return beforeAttributeName;
  } else if (c === "/") {
    currentToken[currentAttribute.name] = currentAttribute.value;
    return selfClosingStartTag;
  } else if (c === ">") {
    currentToken[currentAttribute.name] = currentAttribute.value;
    emit(currentToken);
    return data;
  } else if (c === "\u0000") {
  } else if (c === '"' || c === "'" || c === "<" || c === "=" || c === "`") {
  } else if (c === EOF) {
  } else {
    currentAttribute.value += c;
    return UnqotedAttributeValue;
  }
}

export function parseHTML(html) {
  stack = [{ type: "document", children: [] }];
  currentToken = null;
  currentAttribute = null;
  currentTextNode = null;
  let state = data;
  for (const c of html) {
    state = state(c);
  }
  state = state(EOF);

  return stack[0];
}

const specificityType = {
  inline: 0,
  id: 1,
  class: 2,
  tag: 3
};

function specificity(selector) {
  let p = [0, 0, 0, 0];
  let selectorParts = selector.split(" ");
  for (let part of selectorParts) {
    if (part.charAt(0) === "#") {
      p[specificityType.id] += 1;
    } else if (part.charAt(0) === ".") {
      p[specificityType.class] += 1;
    } else {
      p[specificityType.tag] += 1;
    }
  }

  return p;
}

function compare(sp1, sp2) {
  if (sp1[specificityType.inline] - sp2[specificityType.inline]) {
    return sp1[specificityType.inline] - sp2[specificityType.inline];
  }

  if (sp1[specificityType.id] - sp2[specificityType.id]) {
    return sp1[specificityType.id] - sp2[specificityType.id];
  }

  if (sp1[specificityType.class] - sp2[specificityType.class]) {
    return sp1[specificityType.class] - sp2[specificityType.class];
  }

  return sp1[specificityType.tag] - sp2[specificityType.tag];
}

module.exports = {
  specificity,
  compare
};

function getStyle(element) {
  if (!element.style) element.style = {};

  // console.log("getStyle -> computedStyle=", element.computedStyle);
  for (let prop in element.computedStyle) {
    let p = element.computedStyle.value;
    element.style[prop] = element.computedStyle[prop].value;

    if (element.style[prop].toString().match(/px$/)) {
      element.style[prop] = parseInt(element.style[prop]);
    }

    if (element.style[prop].toString().match(/^[0-9\.]+$/)) {
      element.style[prop] = parseInt(element.style[prop]);
    }
  }

  console.log(
    "getStyle -> element name=",
    element.tagName,
    " style=",
    element.style
  );
  return element.style;
}

function layout(element) {
  // check if computedStyle is empty
  if (
    !element.computedStyle ||
    Object.keys(element.computedStyle).length === 0
  ) {
    return;
  }

  let elementStyle = getStyle(element);

  if (
    elementStyle &&
    elementStyle.display !== null &&
    elementStyle.display !== "flex"
  ) {
    return;
  }

  let items = element.children.filter(e => e.type === "element");

  items.sort((a, b) => {
    return (a.order || 0) - (b.order || 0);
  });

  let style = elementStyle;

  if (style != null && style != undefined) {
    ["width", "height"].forEach(size => {
      if (style[size] === "auto" || style[size] === "") {
        style[size] = null;
      }
    });

    if (!style.flexDirection || style.flexDirection === "auto")
      style.flexDirection = "row";

    if (!style.alignItems || style.alignItems === "auto")
      style.alignItems = "stretch";

    if (!style.justifyContent || style.justifyContent === "auto")
      style.justifyContent = "flex-start";

    if (!style.flexWrap || style.flexWrap === "auto") style.flexWrap = "nowrap";

    if (!style.alignContent || style.alignContent === "auto")
      style.alignContent = "stretch";

    let mainSize,
      mainStart,
      mainEnd,
      mainSign,
      mainBase,
      crossSize,
      crossStart,
      crossEnd,
      crossSign,
      crossBase;

    if (style.flexDirection === "row") {
      mainSize = "width";
      mainStart = "left";
      mainEnd = "right";
      mainSign = +1;
      mainBase = 0;

      crossSize = "height";
      crossStart = "top";
      crossEnd = "bottom";
    }

    if (style.flexDirection === "row-reverse") {
      mainSize = "width";
      mainStart = "right";
      mainEnd = "left";
      mainSign = -1;
      mainBase = 0;

      crossSize = "height";
      crossStart = "top";
      crossEnd = "bottom";
    }

    if (style.flexDirection === "column") {
      mainSize = "height";
      mainStart = "top";
      mainEnd = "bottom";
      mainSign = +1;
      mainBase = 0;

      crossSize = "width";
      crossStart = "left";
      crossEnd = "right";
    }

    if (style.flexDirection === "column-reverse") {
      mainSize = "height";
      mainStart = "bottom";
      mainEnd = "top";
      mainSign = -1;
      mainBase = 0;

      crossSize = "width";
      crossStart = "left";
      crossEnd = "right";
    }

    if (style.flexWrap === "wrap-reverse") {
      let tmp = crossStart;
      crossStart = crossEnd;
      crossEnd = tmp;
      crossSign = -1;
    } else {
      crossBase = 0;
      crossSign = 1;
    }

    let isAutoMainSize = false;
    if (!style[mainSize]) {
      elementStyle[mainSize] = 0;

      for (let i = 0; i < items.length; i++) {
        let item = items[i];
        let itemStyle = getStyle(item);

        if (itemStyle[mainSize] != null || itemStyle[mainSize] !== void 0) {
          elementStyle[mainSize] = elementStyle[mainSize] + itemStyle[mainSize];
        }
      }

      isAutoMainSize = true;
    }

    let flexLine = [];
    let flexLines = [flexLine];

    let mainSpace = elementStyle[mainSize];
    let crossSpace = 0;

    for (let i = 0; i < items.length; i++) {
      let item = items[i];
      let itemStyle = getStyle(item);

      if (itemStyle[mainSize] === null || itemStyle[mainSize] === undefined) {
        itemStyle[mainSize] = 0;
      }

      if (itemStyle.flex) {
        flexLine.push(item);
      } else if (style.flexWrap === "nowrap" && isAutoMainSize) {
        mainSpace -= itemStyle[mainSize];

        if (itemStyle[crossSize] !== null && itemStyle[crossSize] !== void 0) {
          crossSpace = Math.max(crossSpace, itemStyle[crossSize]);
        }

        flexLine.push(item);
      } else {
        if (itemStyle[mainSize] > style[mainSize]) {
          itemStyle[mainSize] = style[mainSize];
        }

        if (mainSpace < itemStyle[mainSize]) {
          flexLine.mainSpace = mainSpace;
          flexLine.crossSpace = crossSpace;
          flexLine = [item];
          flexLines.push(flexLine);
          mainSpace = style[mainSize];
          crossSpace = 0;
        } else {
          flexLine.push(item);
        }

        if (itemStyle[crossSize] !== null && itemStyle[crossSize] !== void 0) {
          crossSpace = Math.max(crossSpace, itemStyle[crossSize]);
        }
        mainSpace -= itemStyle[mainSize];
      }
    }

    flexLine.mainSpace = mainSpace;

    if (style.flexWrap === "nowrap" || isAutoMainSize) {
      flexLine.crossSpace =
        style[crossSize] !== undefined ? style[crossSize] : crossSpace;
    } else {
      flexLine.crossSpace = crossSpace;
    }

    if (mainSpace < 0) {
      // overflow
      let scale = style[mainSize] / (style[mainSize] - mainSpace);
      let currentMain = mainBase;
      for (let i = 0; i < items.length; i++) {
        let item = items[i];
        let itemStyle = getStyle(item);

        if (itemStyle.flex) {
          itemStyle[mainSize] = 0;
        }
        itemStyle[mainSize] = itemStyle[mainSize] * scale;

        itemStyle[mainStart] = currentMain;
        itemStyle[mainEnd] =
          itemStyle[mainStart] + mainSign * itemStyle[mainSize];
        currentMain = itemStyle[mainEnd];
      }
    } else {
      flexLines.forEach(items => {
        let mainSpace = items.mainSpace;
        let flexTotal = 0;
        for (let i = 0; i < items.length; i++) {
          let item = items[i];
          let itemStyle = getStyle(item);

          if (itemStyle.flex !== null && itemStyle.flex !== void 0) {
            flexTotal += itemStyle.flex;
            continue;
          }
        }

        if (flexTotal > 0) {
          let currentMain = mainBase;
          for (let i = 0; i < items.length; i++) {
            let item = items[i];
            let itemStyle = getStyle(item);

            if (itemStyle.flex) {
              itemStyle[mainSize] = (mainSpace / flexTotal) * itemStyle.flex;
            }

            itemStyle[mainStart] = currentMain;
            itemStyle[mainEnd] =
              itemStyle[mainStart] + mainSign * itemStyle[mainSize];
            currentMain = itemStyle[mainEnd];
          }
        } else {
          // no flex items justifyContent

          let currentMain, step;
          if (style.justifyContent === "flex-start") {
            currentMain = mainBase;
            step = 0;
          }

          if (style.justifyContent === "flex-end") {
            currentMain = mainSpace * mainSign + mainBase;
            step = 0;
          }

          if (style.justifyContent === "center") {
            currentMain = (mainSpace / 2) * mainSign + mainBase;
            step = 0;
          }

          if (style.justifyContent === "space-between") {
            step = (mainSpace / (items.length - 1)) * mainSign;
            currentMain = mainBase;
          }

          if (style.justifyContent === "space-around") {
            step = (mainSpace / items.length) * mainSign;
            currentMain = step / 2 + mainBase;
          }

          for (let i = 0; i < items.length; i++) {
            let item = items[i];
            let itemStyle = getStyle(item);
            itemStyle[mainStart] = currentMain;
            itemStyle[mainEnd] =
              itemStyle[mainStart] + mainSign * itemStyle[mainSize];
            currentMain = itemStyle[mainEnd] + step;
          }
        }
      });
    }

    crossSpace = null;
    //compute the cross axis size
    if (!style[crossSize]) {
      // auto sizing
      crossSpace = 0;
      elementStyle[crossSize] = 0;
      for (let i = 0; i < flexLines.length; i++) {
        elementStyle[crossSize] =
          elementStyle[crossSize] + flexLine[i].crossSpace;
      }
    } else {
      crossSpace = style[crossSize];
      for (let i = 0; i < flexLines.length; i++) {
        crossSpace -= flexLines[i].crossSpace;
      }
    }

    if (style.flexWrap === "wrap-reverse") {
      crossBase = style[crossSize];
    } else {
      crossBase = 0;
    }

    let lineSize = style[crossSize] / flexLines.length;
    let step;
    if (style.alignContent === "flex-start") {
      crossBase += 0;
      step = 0;
    }
    if (style.alignContent === "flex-end") {
      crossBase += crossSign * crossSpace;
      step = 0;
    }
    if (style.alignContent === "center") {
      crossBase += (crossSign * crossSpace) / 2;
      step = 0;
    }

    if (style.alignContent === "space-between") {
      crossBase += 0;
      step = crossSpace / (flexLines.length - 1);
    }

    if (style.alignContent === "space-around") {
      step = crossSpace / flexLines.length;
      crossBase += (crossSign * step) / 2;
    }

    if (style.alignContent === "stretch") {
      step = 0;
      crossBase += 0;
    }

    flexLines.forEach(function(items) {
      let lineCrossSize =
        style.alignContent === "stretch"
          ? items.crossSpace + crossSpace / flexLines.length
          : items.crossSpace;

      for (let i = 0; i < items.length; i++) {
        let item = items[i];
        let itemStyle = getStyle(item);

        let align = itemStyle.alignSelf || style.alignItems;

        if (item === null) {
          itemStyle[crossSize] = align === "stretch" ? lineCrossSize : 0;
        }

        if (align === "flex-start") {
          itemStyle[crossStart] = crossBase;
          itemStyle[crossEnd] =
            itemStyle[crossStart] + crossSign * itemStyle[crossSize];
        }

        if (align === "flex-end") {
          itemStyle[crossEnd] = crossBase + crossSign * lineCrossSize;
          itemStyle[crossStart] =
            itemStyle[crossEnd] - crossSign * itemStyle[crossSize];
        }

        if (align === "center") {
          itemStyle[crossStart] =
            crossBase +
            (crossSign * (lineCrossSize - itemStyle[crossSize])) / 2;
          itemStyle[crossEnd] =
            itemStyle[crossStart] + crossSign * itemStyle[crossSize];
        }

        if (align === "stretch") {
          itemStyle[crossStart] = crossBase;
          itemStyle[crossEnd] =
            crossBase +
            crossSign *
              (itemStyle[crossSize] !== null && itemStyle[crossSize] !== void 0
                ? itemStyle[crossSize]
                : lineCrossSize);
          itemStyle[crossSize] =
            crossSign * (itemStyle[crossEnd] - itemStyle[crossStart]);
        }
      }

      crossBase += crossSign * (lineCrossSize + step);
    });
    // console.log("items=", items);
  }
}

module.exports = layout;
